    def runMotors(self, leftRotation,rightRotation):
        leftCycles = int(abs(np.ceil(512*8*leftRotation)))+1
        rightCycles = int(abs(np.ceil(512*8*rightRotation)))+1

        leftDirection = int(np.sign(leftRotation)) * self.L_sign
        rightDirection = int(np.sign(rightRotation))*-1 * self.R_sign

        leftCounter = 0
        rightCounter = 0
        for i in range(max(leftCycles,rightCycles)):
            if leftCounter<=leftCycles:
                self.leftMotor.runStepper(leftDirection)
                leftCounter += 1

            if rightCounter<=rightCycles:
                self.rightMotor.runStepper(rightDirection)
                rightCounter += 1


